int ft_strlen(char *str)
{
	int i = 0;
	while (str[i])
		i++;
	return (i);
}

void wait_fps(int fps)
{
	usleep(1000000 / fps);
}

void ft_putstr(const char *str)
{
	while (*str)
		write(1, &(*(str++)), 1);
}

void ft_strcpy(char *src, char *dst)
{
	while (*src)
	{
		*dst = *src;
		dst++;
		src++;
	}
	*dst = '\0';
}

char *ft_strdup(char *str)
{
	char *ret_str = malloc((ft_strlen(str) + 1) * sizeof(char));
	ft_strcpy(str, ret_str);
	return (ret_str);
}

void free_ptab(char **strs)
{
	int i = 0;
	while (strs[i])
	{
		free(strs[i]);
		strs[i] = NULL;
		i++;
	}
	free(strs);
	strs = NULL;
}

void upper_line(int n)
{
	for (int i = 0 ; i < n ; i++)
	{
		ft_putstr("\033[A");
	}
}

void origin_column()
{
	write(1, "\r", 1);
}

void upper_line_origin(int n)
{
	origin_column();
	upper_line(n);
}

void anime_strs(char **strs, int n, int fps)
{
	while (*strs)
	{
		ft_putstr(*strs);
		if (*(++strs))
			upper_line_origin(n);
		wait_fps(fps);
	}
}

